import { BrowserRouter, Route, Routes } from 'react-router-dom';
import './App.css';
import Home from './componentes/Home';
import Create from './componentes/Create';
import Update from './componentes/Update'; // Importando Update.js

function App() {
  return (
    <BrowserRouter>
      <div className="bg-light min-vh-100 d-flex flex-column">
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
          <div className="container">
            <a className="navbar-brand" href="/">Catálogo de Filmes</a>
            <button
              className="navbar-toggler"
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#navbarNav"
            >
              <span className="navbar-toggler-icon"></span>
            </button>
          </div>
        </nav>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/create" element={<Create />} />
          <Route path="/update/:id" element={<Update />} /> {/* Rota para a edição */}
        </Routes>
        <footer className="text-center mt-auto bg-dark text-white py-3">
          <p>&copy; 2024 Catálogo de Filmes - Todos os direitos reservados</p>
        </footer>
      </div>
    </BrowserRouter>
  );
}

export default App;
