import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import api from '../services/api';

function Home() {
  const [movies, setMovies] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    api.get('/movies')
      .then((response) => setMovies(response.data))
      .catch((error) => console.error('Erro ao buscar filmes:', error));
  }, []);

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Minha Biblioteca de Filmes</h1>
      <div className="d-flex justify-content-center gap-3 mb-4">
        <Link to="/" className="btn btn-primary">Início</Link>
        <Link to="/create" className="btn btn-success">Criar</Link>
        <Link to="/delete" className="btn btn-danger">Apagar</Link>
      </div>
      <div className="row">
        {movies.map((movie) => (
          <div key={movie.id} className="col-md-4 mb-4">
            <div className="card h-100" onClick={() => navigate(`/read/${movie.id}`)}>
              <img src={movie.capa} className="card-img-top" alt={movie.nome} />
              <div className="card-body text-center">
                <h5 className="card-title">{movie.nome}</h5>
                <p className="card-text"><strong>Gênero:</strong> {movie.genero}</p>
                <p className="card-text"><strong>Ano:</strong> {movie.ano}</p>
                <button
                  className="btn btn-warning"
                  onClick={(e) => {
                    e.stopPropagation(); // Evita abrir detalhes ao clicar no botão
                    navigate(`/update/${movie.id}`);
                  }}
                >
                  Alterar
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
