import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../services/api';

function Update() {
  const { id } = useParams();
  const [movie, setMovie] = useState({ name: '', genero: '', ano: '', capa: '' });
  const navigate = useNavigate();

  useEffect(() => {
    api.get(`/${id}`)
      .then((response) => setMovie(response.data))
      .catch((error) => console.error('Erro ao buscar filme:', error));
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setMovie({ ...movie, [name]: value }); // Atualiza o estado corretamente
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      await api.put(`/${id}`, movie); // Envia todo o objeto atualizado para a API
      alert('Filme atualizado com sucesso!');
      navigate('/');
    } catch (error) {
      console.error('Erro ao atualizar filme:', error);
      alert('Erro ao atualizar filme. Tente novamente.');
    }
  };

  if (!movie) return <p>Carregando...</p>;

  return (
    <div className="container mt-5">
      <h1 className="text-center">Atualizar Filme</h1>
      <form onSubmit={handleUpdate} className="mt-4">
        <div className="mb-3">
          <label className="form-label">Nome</label>
          <input
            type="text"
            name="name"
            className="form-control"
            value={movie.name}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Gênero</label>
          <input
            type="text"
            name="genero"
            className="form-control"
            value={movie.genero}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Ano</label>
          <input
            type="number"
            name="ano"
            className="form-control"
            value={movie.ano}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Capa (URL)</label>
          <input
            type="text"
            name="capa"
            className="form-control"
            value={movie.capa}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" className="btn btn-warning w-100">
          Atualizar Filme
        </button>
      </form>
    </div>
  );
}

export default Update;
