import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import api from '../services/api'; // Importa a MockAPI configurada

function Home() {
  const [movies, setMovies] = useState([]);
  const [deletedMovieId, setDeletedMovieId] = useState(null); // Guarda o ID do filme a ser excluído
  const navigate = useNavigate();

  useEffect(() => {
    api.get('/movies')
      .then((response) => setMovies(response.data))
      .catch((error) => console.error('Erro ao buscar filmes:', error));
  }, []);

  const handleDelete = async (id) => {
    if (window.confirm('Tem certeza que deseja apagar este filme?')) {
      setDeletedMovieId(id); // Armazena o ID do filme que será excluído
      const card = document.getElementById(`movie-${id}`); // Seleciona o card do filme
      card.classList.add('explode'); // Adiciona a classe de explosão

      // Espera a animação de explosão terminar
      setTimeout(async () => {
        try {
          await api.delete(`/movies/${id}`); // Remove da MockAPI
          setMovies((prevMovies) =>
            prevMovies.filter((movie) => movie.id !== id)
          );
          setDeletedMovieId(null); // Limpa o ID do filme excluído
        } catch (error) {
          console.error('Erro ao apagar filme:', error);
        }
      }, 500); // 500ms é o tempo da animação de explosão
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Biblioteca de Filmes</h1>
      <Link to="/create" className="btn btn-primary mb-3">
        Adicionar Filme
      </Link>
      <div className="row">
        {movies.map((movie, index) => (
          <div
            key={movie.id}
            id={`movie-${movie.id}`}
            className={`col-md-4 mb-4 ${
              deletedMovieId === null ? 'slide-up' : ''
            }`} // Aplica 'slide-up' se um filme foi removido
          >
            <div className="card shadow-lg">
              <img src={movie.capa} className="card-img-top" alt={movie.nome} />
              <div className="card-body">
                <h5 className="card-title">{movie.nome}</h5>
                <p className="card-text">
                  <strong>Gênero:</strong> {movie.genero}
                </p>
                <p className="card-text">
                  <strong>Ano:</strong> {movie.ano}
                </p>
                <div className="d-flex gap-2">
                  <button
                    className="btn btn-success w-50"
                    onClick={() => navigate(`/update/${movie.id}`)}
                  >
                    Alterar
                  </button>
                  <button
                    className="btn btn-danger w-50"
                    onClick={() => handleDelete(movie.id)}
                  >
                    Apagar
                  </button>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
