import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import api from '../services/api';

function Home() {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    api.get('/movies')
      .then(response => setMovies(response.data))
      .catch(error => console.log(error));
  }, []);

  const handleDelete = async (id) => {
    if (window.confirm('Tem certeza que deseja apagar este filme?')) {
      try {
        await api.delete(`/movies/${id}`);
        setMovies(movies.filter((movie) => movie.id !== id));
      } catch (error) {
        console.error('Erro ao apagar filme:', error);
        alert('Erro ao apagar o filme. Tente novamente.');
      }
    }
  };

  return (
    <div className="container mt-5">
      <h1>Biblioteca de Filmes</h1>
      <Link to="/create" className="btn btn-primary mb-3">Adicionar Filme</Link>

      <table className="table table-striped">
        <thead>
          <tr>
            <th>ID</th>
            <th>Nome</th>
            <th>Gênero</th>
            <th>Ano</th>
            <th>Diretor</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {movies.map((movie) => (
            <tr key={movie.id}>
              <td>{movie.id}</td>
              <td>{movie.nome}</td>
              <td>{movie.genero}</td>
              <td>{movie.ano}</td>
              <td>{movie.diretor}</td>
              <td>
                <Link to={`/read/${movie.id}`} className="btn btn-info btn-sm me-2">Ler</Link>
                <Link to={`/update/${movie.id}`} className="btn btn-warning btn-sm me-2">Editar</Link>
                <button className="btn btn-danger btn-sm" onClick={() => handleDelete(movie.id)}>
                  Apagar
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Home;
