import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

function Home() {
  const [data, setData] = useState([]);

  useEffect(() => {
    axios.get('https://66d22bfd62816af9a4f6045d.mockapi.io/users')
      .then(res => setData(res.data))
      .catch(err => console.log(err));
  }, []);

  const handleDelete = (id) => {
    if (window.confirm("Quer mesmo apagar este usuário?")) {
      axios.delete(`https://66d22bfd62816af9a4f6045d.mockapi.io/users/${id}`)
        .then(() => setData(prevData => prevData.filter(user => user.id !== id)))
        .catch(err => console.log(err));
    }
  };

  return (
    <div className='d-flex flex-column justify-content-center align-items-center bg-light vh-100'>
      <h1>Lista de Usuários</h1>
      <div className='table-container'>
        <table className='table table-striped'>
          <thead>
            <tr>
              <th>ID</th>
              <th>Nome</th>
              <th>Email</th>
              <th>Celular</th>
              <th>Ação</th>
            </tr>
          </thead>
          <tbody>
            {data.map((user, index) => (
              <tr key={index}>
                <td>{user.id}</td>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>{user.phone}</td>
                <td>
                  <Link to={`/read/${user.id}`} className='btn btn-sm btn-info me-2'>Ler</Link>
                  <Link to={`/update/${user.id}`} className='btn btn-sm btn-primary me-2'>Editar</Link>
                  <button onClick={() => handleDelete(user.id)} className='btn btn-sm btn-danger'>Apagar</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <div className='d-flex justify-content-end'>
          <Link to="/create" className='btn btn-success'>Novo</Link>
        </div>
      </div>
    </div>
  );
}

export default Home;
