import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import api from '../services/api'; // Importa a MockAPI configurada

function Home() {
  const [movies, setMovies] = useState([]);
  const navigate = useNavigate(); // Para redirecionamento

  useEffect(() => {
    api.get('/movies')
      .then((response) => setMovies(response.data))
      .catch((error) => console.error('Erro ao buscar filmes:', error));
  }, []);

  const handleDelete = async (id) => {
    if (window.confirm('Tem certeza que deseja apagar este filme?')) {
      try {
        await api.delete(`/movies/${id}`);
        setMovies((prev) => prev.filter((movie) => movie.id !== id));
      } catch (error) {
        console.error('Erro ao apagar filme:', error);
      }
    }
  };

  const handleUpdate = (id) => {
    navigate(`/update/${id}`); // Redireciona para a página de edição
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Biblioteca de Filmes</h1>
      <Link to="/create" className="btn btn-primary mb-3">
        Adicionar Filme
      </Link>
      <div className="row">
        {movies.map((movie) => (
          <div key={movie.id} className="col-md-4 mb-4">
            <div className="card shadow-lg">
              <img src={movie.capa} className="card-img-top" alt={movie.nome} />
              <div className="card-body">
                <h5 className="card-title">{movie.nome}</h5>
                <p className="card-text"><strong>Gênero:</strong> {movie.genero}</p>
                <p className="card-text"><strong>Ano:</strong> {movie.ano}</p>
                <div className="d-flex gap-2">
                  <button
                    className="btn btn-success w-50"
                    onClick={() => handleUpdate(movie.id)}
                  >
                    Alterar
                  </button>
                  <button
                    className="btn btn-danger w-50"
                    onClick={() => handleDelete(movie.id)}
                  >
                    Apagar
                  </button>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
