import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

function Update() {
  const [id, setId] = useState('');
  const [movie, setMovie] = useState(null);  // Estado para armazenar os dados do filme
  const navigate = useNavigate();

  // Função para buscar os dados do filme pelo ID
  const handleSearch = async () => {
    try {
      const response = await api.get(`/${id}`);
      setMovie(response.data);  // Armazena o filme encontrado
    } catch (error) {
      console.error('Erro ao buscar filme:', error);
      alert('Filme não encontrado!');
    }
  };

  // Função para atualizar os dados do filme
  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      await api.put(`/${id}`, movie);
      alert('Filme atualizado com sucesso!');
      navigate('/');
    } catch (error) {
      console.error('Erro ao atualizar filme:', error);
      alert('Erro ao atualizar filme. Tente novamente.');
    }
  };

  const handleChange = (e) => {
    setMovie({ ...movie, [e.target.name]: e.target.value });
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center">Alterar Filme</h1>

      {/* Solicitação do ID */}
      {!movie && (
        <>
          <input
            type="text"
            className="form-control mb-3"
            placeholder="Digite o ID do filme"
            value={id}
            onChange={(e) => setId(e.target.value)}
          />
          <button className="btn btn-primary w-100" onClick={handleSearch}>
            Buscar Filme
          </button>
        </>
      )}

      {/* Formulário de Edição (aparece após buscar o filme) */}
      {movie && (
        <form onSubmit={handleUpdate}>
          <div className="mb-3">
            <label className="form-label">Nome</label>
            <input
              type="text"
              name="nome"
              className="form-control"
              value={movie.nome}
              onChange={handleChange}
              required
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Gênero</label>
            <input
              type="text"
              name="genero"
              className="form-control"
              value={movie.genero}
              onChange={handleChange}
              required
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Ano</label>
            <input
              type="number"
              name="ano"
              className="form-control"
              value={movie.ano}
              onChange={handleChange}
              required
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Capa (URL)</label>
            <input
              type="text"
              name="capa"
              className="form-control"
              value={movie.capa}
              onChange={handleChange}
              required
            />
          </div>
          <button type="submit" className="btn btn-warning w-100">
            Atualizar Filme
          </button>
        </form>
      )}
    </div>
  );
}

export default Update;
