import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import api from '../services/api';

function Create() {
  const [movie, setMovie] = useState({ nome: '', genero: '', ano: '' });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setMovie({ ...movie, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    api.post('/movies', movie)
      .then(() => navigate('/'))
      .catch(error => console.log(error));
  };

  return (
    <div className="container mt-5">
      <h1>Adicionar Filme</h1>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label>Nome</label>
          <input type="text" name="nome" className="form-control" onChange={handleChange} required />
        </div>
        <div className="mb-3">
          <label>GÃªnero</label>
          <input type="text" name="genero" className="form-control" onChange={handleChange} required />
        </div>
        <div className="mb-3">
          <label>Ano</label>
          <input type="text" name="ano" className="form-control" onChange={handleChange} required />
        </div>
        <button className="btn btn-success">Adicionar</button>
        <Link to="/" className="btn btn-secondary ms-3">Cancelar</Link>
      </form>
    </div>
  );
}

export default Create;
