import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import api from '../services/api'; // MockAPI configurada no api.js

function Create() {
  const [values, setValues] = useState({
    nome: '',
    genero: '',
    ano: '',
    capa: ''
  });

  const navigate = useNavigate(); // Para redirecionar para a página inicial

  const handleChange = (e) => {
    setValues({ ...values, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.post('/movies', values);
      alert('Filme adicionado com sucesso!');
      navigate('/'); // Redireciona para a página inicial
    } catch (error) {
      console.error('Erro ao adicionar filme:', error);
      alert('Erro ao adicionar filme. Tente novamente.');
    }
  };

  return (
    <div className="container mt-5">
      <h1>Adicionar Filme</h1>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label>Nome</label>
          <input
            type="text"
            name="nome"
            className="form-control"
            placeholder="Nome do Filme"
            value={values.nome}
            onChange={handleChange}
            required
          />
        </div>

        <div className="mb-3">
          <label>Gênero</label>
          <input
            type="text"
            name="genero"
            className="form-control"
            placeholder="Gênero do Filme"
            value={values.genero}
            onChange={handleChange}
            required
          />
        </div>

        <div className="mb-3">
          <label>Ano</label>
          <input
            type="text"
            name="ano"
            className="form-control"
            placeholder="Ano de Lançamento"
            value={values.ano}
            onChange={handleChange}
            required
          />
        </div>

        <div className="mb-3">
          <label>Capa (URL)</label>
          <input
            type="text"
            name="capa"
            className="form-control"
            placeholder="URL da Capa do Filme"
            value={values.capa}
            onChange={handleChange}
            required
          />
        </div>

        <button type="submit" className="btn btn-success">
          Adicionar Filme
        </button>
        <Link to="/" className="btn btn-secondary ms-3">
          Cancelar
        </Link>
      </form>
    </div>
  );
}

export default Create;
