import axios from 'axios'; // Mantemos axios aqui para simplicidade
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

function Create() {
  const [values, setValues] = useState({ nome: '', genero: '', ano: '' });
  const navigate = useNavigate(); // Redireciona após adicionar o filme

  const handleChange = (e) => {
    setValues({ ...values, [e.target.name]: e.target.value });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Usando Axios diretamente como antes
    axios.post('https://66d22bfd62816af9a4f6045d.mockapi.io/movies', values)
      .then(() => {
        console.log('Filme adicionado com sucesso!');
        navigate('/'); // Redireciona para a página inicial após adicionar
      })
      .catch((error) => {
        console.error('Erro ao adicionar o filme:', error);
        alert('Ocorreu um erro ao adicionar o filme. Verifique os dados e tente novamente.');
      });
  };

  return (
    <div className="container mt-5">
      <h1>Adicionar Filme</h1>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label>Nome</label>
          <input
            type="text"
            name="nome"
            className="form-control"
            value={values.nome}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label>Gênero</label>
          <input
            type="text"
            name="genero"
            className="form-control"
            value={values.genero}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label>Ano</label>
          <input
            type="text"
            name="ano"
            className="form-control"
            value={values.ano}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" className="btn btn-success">Adicionar</button>
        <Link to="/" className="btn btn-secondary ms-3">Cancelar</Link>
      </form>
    </div>
  );
}

export default Create;
