import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

function Create() {
  const [movie, setMovie] = useState({ nome: '', genero: '', ano: '', capa: '' });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setMovie({ ...movie, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!movie.nome || !movie.genero || !movie.ano || !movie.capa) {
      alert('Todos os campos são obrigatórios!');
      return;
    }
    try {
      await api.post('/movies', movie);
      navigate('/');
    } catch (error) {
      console.error('Erro ao adicionar filme:', error);
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center">Adicionar Filme</h1>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label>Nome</label>
          <input type="text" name="nome" className="form-control" onChange={handleChange} required />
        </div>
        <div className="mb-3">
          <label>Gênero</label>
          <input type="text" name="genero" className="form-control" onChange={handleChange} required />
        </div>
        <div className="mb-3">
          <label>Ano</label>
          <input type="number" name="ano" className="form-control" onChange={handleChange} required />
        </div>
        <div className="mb-3">
          <label>Capa (URL)</label>
          <input type="text" name="capa" className="form-control" onChange={handleChange} required />
        </div>
        <button type="submit" className="btn btn-success w-100">Adicionar</button>
      </form>
    </div>
  );
}

export default Create;
