import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

function DeletePage() {
  const [id, setId] = useState('');
  const navigate = useNavigate();

  const handleDelete = async () => {
    if (!id) {
      alert('Por favor, insira um ID v치lido.');
      return;
    }
    try {
      await api.delete(`/movies/${id}`);
      alert('Filme apagado com sucesso!');
      navigate('/'); // Redireciona para a Home ap칩s apagar
    } catch (error) {
      alert('Erro ao apagar o filme.');
      console.error(error);
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center">Apagar Filme</h1>
      <input
        type="text"
        className="form-control mb-3"
        placeholder="Digite o ID do filme"
        value={id}
        onChange={(e) => setId(e.target.value)}
      />
      <button className="btn btn-danger w-100" onClick={handleDelete}>
        Apagar Filme
      </button>
    </div>
  );
}

export default DeletePage;
