import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import api from '../services/api'; // MockAPI configurada

function Create() {
  const [values, setValues] = useState({
    nome: '',
    genero: '',
    ano: '',
    capa: ''
  });
  const navigate = useNavigate();

  // Atualiza os valores no estado conforme o usuário digita
  const handleChange = (e) => {
    setValues({ ...values, [e.target.name]: e.target.value });
  };

  // Envia o formulário para a MockAPI
  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Enviando dados:', values); // Verifique o conteúdo no console

    try {
      // Envia a requisição POST para a MockAPI
      await api.post('/movies', values);
      alert('Filme adicionado com sucesso!');
      navigate('/'); // Redireciona para a página inicial
    } catch (error) {
      console.error('Erro ao adicionar filme:', error);
      alert('Erro ao adicionar filme. Verifique os dados e tente novamente.');
    }
  };

  return (
    <div className="container mt-5">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="card shadow-lg">
            <div className="card-header bg-dark text-white">
              <h3>Adicionar Filme</h3>
            </div>
            <div className="card-body">
              <form onSubmit={handleSubmit}>
                <div className="mb-3">
                  <label className="form-label">Nome</label>
                  <input
                    type="text"
                    name="nome"
                    className="form-control"
                    placeholder="Nome do Filme"
                    value={values.nome}
                    onChange={handleChange}
                    required
                  />
                </div>

                <div className="mb-3">
                  <label className="form-label">Gênero</label>
                  <input
                    type="text"
                    name="genero"
                    className="form-control"
                    placeholder="Gênero do Filme"
                    value={values.genero}
                    onChange={handleChange}
                    required
                  />
                </div>

                <div className="mb-3">
                  <label className="form-label">Ano</label>
                  <input
                    type="text"
                    name="ano"
                    className="form-control"
                    placeholder="Ano de Lançamento"
                    value={values.ano}
                    onChange={handleChange}
                    required
                  />
                </div>

                <div className="mb-3">
                  <label className="form-label">Capa (URL)</label>
                  <input
                    type="text"
                    name="capa"
                    className="form-control"
                    placeholder="URL da Capa do Filme"
                    value={values.capa}
                    onChange={handleChange}
                    required
                  />
                </div>

                <button type="submit" className="btn btn-success w-100">
                  Adicionar Filme
                </button>
                <Link to="/" className="btn btn-secondary w-100 mt-2">
                  Cancelar
                </Link>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Create;
