import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import api from '../services/api'; // MockAPI

const API_KEY = 'defcbb2f'; // Substitua pela sua chave OMDb API

function Create() {
  const [search, setSearch] = useState(''); // Campo de busca
  const [movie, setMovie] = useState(null); // Dados do filme encontrado
  const navigate = useNavigate();

  const handleSearch = async () => {
    try {
      const response = await axios.get(`https://www.omdbapi.com/?t=${search}&apikey=${API_KEY}`);
      if (response.data && response.data.Response === 'True') {
        setMovie(response.data);
      } else {
        alert('Filme não encontrado. Tente outro título.');
      }
    } catch (error) {
      console.error('Erro ao buscar filme:', error);
      alert('Ocorreu um erro na busca. Tente novamente.');
    }
  };

  const handleAddMovie = async () => {
    if (movie) {
      try {
        await api.post('/movies', {
          nome: movie.Title,
          genero: movie.Genre,
          ano: movie.Year,
          diretor: movie.Director,
          sinopse: movie.Plot,
        });
        alert('Filme adicionado à biblioteca!');
        navigate('/'); // Redireciona para a página inicial
      } catch (error) {
        console.error('Erro ao adicionar filme:', error);
        alert('Erro ao adicionar filme. Tente novamente.');
      }
    }
  };

  return (
    <div className="container mt-5">
      <h1>Adicionar Filme</h1>

      {/* Campo de Busca */}
      <div className="input-group mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Digite o nome do filme"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <button className="btn btn-primary" onClick={handleSearch}>
          Buscar
        </button>
      </div>

      {/* Exibir informações do filme encontrado */}
      {movie && (
        <div className="card mt-3">
          <div className="card-body">
            <h5 className="card-title">{movie.Title}</h5>
            <p className="card-text"><strong>Gênero:</strong> {movie.Genre}</p>
            <p className="card-text"><strong>Ano:</strong> {movie.Year}</p>
            <p className="card-text"><strong>Diretor:</strong> {movie.Director}</p>
            <p className="card-text"><strong>Sinopse:</strong> {movie.Plot}</p>
            <button className="btn btn-success" onClick={handleAddMovie}>
              Adicionar à Biblioteca
            </button>
          </div>
        </div>
      )}

      <Link to="/" className="btn btn-secondary mt-3">Cancelar</Link>
    </div>
  );
}

export default Create;
