import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import api from '../services/api';

function Home() {
  const [movies, setMovies] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    api.get('/movies')
      .then((response) => setMovies(response.data))
      .catch((error) => console.error('Erro ao buscar filmes:', error));
  }, []);

  const handleDelete = async (id) => {
    if (window.confirm('Tem certeza que deseja apagar este filme?')) {
      try {
        await api.delete(`/movies/${id}`);
        setMovies((prevMovies) => prevMovies.filter((movie) => movie.id !== id));
      } catch (error) {
        console.error('Erro ao apagar filme:', error);
      }
    }
  };

  return (
    <div className="container">
      <h1 className="text-center mb-4">Minha Biblioteca de Filmes</h1>
      <Link to="/create" className="btn btn-primary mb-3">Adicionar Filme</Link>
      <div className="row">
        {movies.map((movie) => (
          <div key={movie.id} className="card">
            <img src={movie.capa} alt={movie.nome} className="card-img-top" />
            <div className="card-body">
              <h5 className="card-title">{movie.nome}</h5>
              <p><strong>GÃªnero:</strong> {movie.genero}</p>
              <p><strong>Ano:</strong> {movie.ano}</p>
              <div className="d-flex justify-content-between">
                <button className="btn btn-success" onClick={() => navigate(`/update/${movie.id}`)}>Alterar</button>
                <button className="btn btn-danger" onClick={() => handleDelete(movie.id)}>Apagar</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
