import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

function Create() {
  const [movie, setMovie] = useState({ id: '', nome: '', genero: '', ano: '', capa: '' });
  const [existingMovies, setExistingMovies] = useState([]);
  const navigate = useNavigate();

  // Carrega todos os filmes para verificar IDs existentes
  useEffect(() => {
    api.get('/movies')
      .then((response) => setExistingMovies(response.data))
      .catch((error) => console.error('Erro ao carregar filmes:', error));
  }, []);

  const handleChange = (e) => {
    setMovie({ ...movie, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Verifica se o ID já existe
    const idExists = existingMovies.some((m) => m.id === movie.id);
    if (idExists) {
      alert('Erro: Já existe um filme com este ID.');
      return;
    }

    // Verifica se todos os campos estão preenchidos
    if (!movie.nome || !movie.genero || !movie.ano || !movie.capa || !movie.id) {
      alert('Todos os campos são obrigatórios!');
      return;
    }

    try {
      await api.post('/movies', movie);  // Envia os dados para a API
      alert('Filme criado com sucesso!');
      navigate('/');  // Redireciona para a página inicial
    } catch (error) {
      console.error('Erro ao adicionar filme:', error);
      alert('Erro ao adicionar filme. Tente novamente.');
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center">Adicionar Filme</h1>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="form-label">ID</label>
          <input
            type="text"
            name="id"
            className="form-control"
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Nome</label>
          <input
            type="text"
            name="nome"
            className="form-control"
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Gênero</label>
          <input
            type="text"
            name="genero"
            className="form-control"
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Ano</label>
          <input
            type="number"
            name="ano"
            className="form-control"
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Capa (URL)</label>
          <input
            type="text"
            name="capa"
            className="form-control"
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" className="btn btn-success w-100">
          Adicionar Filme
        </button>
      </form>
    </div>
  );
}

export default Create;
