import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../services/api';

function Read() {
  const { id } = useParams();
  const [movie, setMovie] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    api.get(`/movies/${id}`)
      .then((response) => setMovie(response.data))
      .catch((error) => console.error('Erro ao buscar filme:', error));
  }, [id]);

  return (
    <div className="container mt-5">
      <h1 className="text-center">Detalhes do Filme</h1>
      <div className="card">
        <img src={movie.capa} className="card-img-top" alt={movie.nome} />
        <div className="card-body">
          <h5 className="card-title">{movie.nome}</h5>
          <p><strong>GÃªnero:</strong> {movie.genero}</p>
          <p><strong>Ano:</strong> {movie.ano}</p>
          <button className="btn btn-primary" onClick={() => navigate('/')}>
            Voltar
          </button>
        </div>
      </div>
    </div>
  );
}

export default Read;
