import React, { useEffect, useState } from 'react';
import { Link, useNavigate, useParams } from 'react-router-dom';
import api from '../services/api'; // Importa a MockAPI configurada

function Update() {
  const { id } = useParams(); // Obtém o ID do filme da URL
  const [values, setValues] = useState({
    nome: '',
    genero: '',
    ano: '',
    capa: ''
  });
  const navigate = useNavigate();

  useEffect(() => {
    // Busca os dados do filme a ser editado e preenche o formulário
    api.get(`/movies/${id}`)
      .then((response) => setValues(response.data))
      .catch((error) => console.error('Erro ao buscar filme:', error));
  }, [id]);

  const handleChange = (e) => {
    setValues({ ...values, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Envia a atualização para a MockAPI
      await api.put(`/movies/${id}`, values);
      alert('Filme atualizado com sucesso!');
      navigate('/'); // Redireciona para a página inicial
    } catch (error) {
      console.error('Erro ao atualizar filme:', error);
      alert('Erro ao atualizar filme. Tente novamente.');
    }
  };

  return (
    <div className="container mt-5">
      <h1>Editar Filme</h1>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label>Nome</label>
          <input
            type="text"
            name="nome"
            className="form-control"
            placeholder="Nome do Filme"
            value={values.nome}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label>Gênero</label>
          <input
            type="text"
            name="genero"
            className="form-control"
            placeholder="Gênero do Filme"
            value={values.genero}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label>Ano</label>
          <input
            type="text"
            name="ano"
            className="form-control"
            placeholder="Ano de Lançamento"
            value={values.ano}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label>Capa (URL)</label>
          <input
            type="text"
            name="capa"
            className="form-control"
            placeholder="URL da Capa do Filme"
            value={values.capa}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" className="btn btn-success w-100">
          Atualizar Filme
        </button>
        <Link to="/" className="btn btn-secondary w-100 mt-2">
          Cancelar
        </Link>
      </form>
    </div>
  );
}

export default Update;
