import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../services/api';

function Update() {
  const { id } = useParams(); // Obtém o ID da URL
  const [movie, setMovie] = useState({ nome: '', genero: '', ano: '', capa: '' });
  const navigate = useNavigate();

  // Carrega os dados do filme pelo ID
  useEffect(() => {
    api.get(`/movies/${id}`)
      .then((response) => setMovie(response.data))
      .catch((error) => console.error('Erro ao buscar filme:', error));
  }, [id]);

  const handleChange = (e) => {
    setMovie({ ...movie, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.put(`/movies/${id}`, movie);
      alert('Filme atualizado com sucesso!');
      navigate('/'); // Redireciona para a página inicial
    } catch (error) {
      console.error('Erro ao atualizar filme:', error);
      alert('Erro ao atualizar filme. Tente novamente.');
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center">Alterar Filme</h1>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="form-label">Nome</label>
          <input
            type="text"
            name="nome"
            className="form-control"
            value={movie.nome}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Gênero</label>
          <input
            type="text"
            name="genero"
            className="form-control"
            value={movie.genero}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Ano</label>
          <input
            type="number"
            name="ano"
            className="form-control"
            value={movie.ano}
            onChange={handleChange}
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Capa (URL)</label>
          <input
            type="text"
            name="capa"
            className="form-control"
            value={movie.capa}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" className="btn btn-warning w-100">
          Atualizar
        </button>
      </form>
    </div>
  );
}

export default Update;
