import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

function Create() {
  const [movie, setMovie] = useState({ nome: '', genero: '', ano: '', capa: '' });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setMovie({ ...movie, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!movie.nome || !movie.genero || !movie.ano || !movie.capa) {
      alert('Todos os campos são obrigatórios!');
      return;
    }
    try {
      await api.post('/', movie);
      alert('Filme criado com sucesso!');
      navigate('/');
    } catch (error) {
      console.error('Erro ao adicionar filme:', error);
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center">Adicionar Filme</h1>
      <form onSubmit={handleSubmit}>
        <input name="nome" placeholder="Nome" onChange={handleChange} required />
        <input name="genero" placeholder="Gênero" onChange={handleChange} required />
        <input name="ano" placeholder="Ano" onChange={handleChange} required />
        <input name="capa" placeholder="URL da Capa" onChange={handleChange} required />
        <button type="submit">Adicionar Filme</button>
      </form>
    </div>
  );
}

export default Create;
